package ir.home.tutorial.algorithm.leetcode.contest.weekly.weeklycontest228.q3;

import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.assertj.core.api.Assertions.assertThat;
import static org.junit.jupiter.params.provider.Arguments.arguments;

class SolutionTest {

    private static Solution solution;

    @BeforeAll
    static void setUp() {
        solution = new Solution();
    }


    static Stream<Arguments> leetcodeSampleArrays() {
        return Stream.of(
                arguments("leetcode", 7, 20, 2, 0, "ee"),
                arguments("fbxzaad", 31, 100, 3, 32, "fbx"),
                arguments("zahrabaranyasin", 2, 100, 5, 29, "ahrab"),
                arguments("zahrabaranyasin", 2, 100, 5, 8, "baran"),
                arguments("xqgfatvtlwnnkxipmipcpqwbxihxblaplpfckvxtihonijhtezdnkjmmk", 22, 51, 41, 9, "xqgfatvtlwnnkxipmipcpqwbxihxblaplpfckvxti"),
                arguments("", 71717, 94536, 1149, 39999, "xqgfatvtlwnnkxipmipcpqwbxihxblaplpfckvxti")
        );
    }


    @ParameterizedTest
    @MethodSource("leetcodeSampleArrays")
    void testLeetcodeSampleArrays(String str, int power, int modulo, int k, int hashValue, String expected) {
        final String rearrangedArray = solution.subStrHash2(str, power, modulo, k, hashValue);

        assertThat(rearrangedArray).isEqualTo(expected);
    }
}